const ArgumentType = require('../../extension-support/argument-type');
const BlockType = require('../../extension-support/block-type');
const Cast = require('../../util/cast');
const log = require('../../util/log');

// Values
var bankNum;
var text_sha256;
var id_;
const ext_version = "NumberBank 0.1.0";


 /**
 * Icon svg to be displayed at the left edge of each extension block, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const blockIconURI = 'data:image/svg+xml;base64,';

/**
 * Icon svg to be displayed in the category menu, encoded as a data URI.
 * @type {string}
 */
// eslint-disable-next-line max-len
const menuIconURI = 'data:image/svg+xml;base64,';



/**
 * Class for the NumberBank with Scratch 3.0
 * @param {Runtime} runtime - the runtime instantiating this block package.
 * @constructor
 */
class Scratch3Numberbank {
    constructor (runtime) {
        /**
         * The runtime instantiating this block package.
         * @type {Runtime}
         */
        this.runtime = runtime;

//      console.log("initializing...");
//		console.log("version:");
		console.log(ext_version);


//      console.log("init_done");

}


    /**
     * @returns {object} metadata for this extension and its blocks.
     */
    getInfo () {
        return {
            id: 'numberbank',
            name: 'NumberBank',
            menuIconURI: menuIconURI,
            blockIconURI: blockIconURI,
            blocks: [
                {
                    opcode: 'convertSha256',
                    blockType: BlockType.COMMAND,
                    text: 'sha256 [TEXT]',
                    arguments: {
                        TEXT: {
                            type: ArgumentType.STRING,
                            defaultValue: "hello"
                        }
                    }
                },
                {
                    opcode: 'getHashText',
                    text: 'getHashText',
                    blockType: BlockType.REPORTER
                }
/**
                ,
                {
                    opcode: 'openPasori',
                    text: 'open PaSoRi',
                    blockType: BlockType.COMMAND,
                    text: 'open PaSoRi'
                }
                ,
                {
                    opcode: 'closePasori',
                    text: 'close PaSoRi',
                    blockType: BlockType.COMMAND,
                    text: 'close PaSoRi'
                }
*/
            ],
            menus: {
            }
        };
    }

    convertSha256 (args) {

        const byteArray = new Uint8Array(args.TEXT);
        const hexCodes = [...byteArray].map(value => {
          const hexCode = value.toString(16);
          const paddedHexCode = hexCode.padStart(2, '0');
          return paddedHexCode;
        });
        return text_sha256 = hexCodes.join('');
        
    }

    /**
     * Get the browser.
     * @return {number} - the user agent.
     */
    getBrowser () {
        return navigator.userAgent;
    }


    getIdm () {
//		console.log('=== S:getIdm ===');
        return idnum;
    }

    
    getHashText () {

        return text_sha256;
    }
        
    


}

module.exports = Scratch3Numberbank;
